$(document).ready(function()
{
    new slider('.color-changer', updateGlow);
    if (typeof touchHandler === 'function')
    {
        $('.color-changer').on("touchstart touchmove touchend touchcancel", touchHandler);
    }
});

var slider = function(targetElement, callback)
{
    var self = this;
    self.target = $(targetElement);
    self.width  = self.target.width();
    self.limits = {left: self.target.offset().left, right: self.target.offset().left + self.width};
    self.handle = self.target.find('.handle');
    self.bindEvents = function()
    {
        $(window).on('resize', function()
        {
            self.width = self.target.width();
            self.limits = {left: self.target.offset().left, right: self.target.offset().left + self.width};
        });

        self.target
            .on('click', function(e)
            {
                self.updatePosition(e);
            });
        self.target
            .on('mousedown', function(e)
            {
                self.updatePosition(e);

                // Attach mouse move event only when first clicked.
                $(document).on('mousemove', function(e)
                {
                    self.updatePosition(e);
                })
                .one('mouseup', function(e)
                {
                    $(document).off('mousemove');
                });
            });

        self.updatePosition = function(e)
        {
            // var handlePosition = Math.max(Math.min(e.pageX, self.limits.right), self.limits.left);
            var handlePosition = Math.max(Math.min(e.pageX, self.limits.right), self.limits.left),
                handlePosPercent = (handlePosition - self.limits.left) / self.width * 100;

            self.handle.css({left: handlePosPercent + '%'});

            if (typeof callback === 'function') callback(handlePosPercent);
        };
    };
    self.init = function()
    {
        self.bindEvents();
    }();
}

var updateGlow = function(left)
{
    $('.breakout5 .glow').css('background-position', left + '% center');
}
var touchHandler = function(event)
{
    var touch = event.hasOwnProperty('changedTouches') ? event.changedTouches[0] : event.originalEvent.changedTouches[0];

    var simulatedEvent = document.createEvent("MouseEvent");
        simulatedEvent.initMouseEvent({
        touchstart: "mousedown",
        touchmove: "mousemove",
        touchend: "mouseup"
    }[event.type], true, true, window, 1,
        touch.screenX, touch.screenY,
        touch.clientX, touch.clientY, false,
        false, false, false, 0, null);

    touch.target.dispatchEvent(simulatedEvent);

    event.preventDefault();
};

