@media screen and (max-width: 550px) {
    #features .breakout .col {width: 100%;}
}
.breakout5 {
    background: #000;
    text-align: center;
}
.breakout5 .mouse-wrapper {
    position: relative;
}

.breakout5 .mouse-img-wrapper {display: inline-block;position: relative;}
.breakout5 .mouse-wrapper img {
    position: relative;
    z-index: 2;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -o-user-select: none;
    user-select: none;
}
.breakout5 .mouse-wrapper #gradient,
.breakout5 .mouse-wrapper .glow {
    position: absolute;
    top: 20px;
    left: 14%;
    right: 4%;
    bottom: 20px;
    z-index: 0;
}
.breakout5 .mouse-color-changer {padding: 30px 0 50px;}
.breakout5 .mouse-color-changer img ~ #gradient,
.breakout5 .mouse-color-changer img ~ .glow {
    opacity: 0;
    -webkit-transition: .3s 1s opacity;
    -o-transition: .3s 1s opacity;
    transition: .3s 1s opacity;
}
.breakout5 .mouse-color-changer img ~ #gradient,
.breakout5 .mouse-color-changer img ~ .glow {opacity: 1;}

.breakout5 .mouse-color-changer img ~ #gradient {
    opacity: 1;
    z-index: 1;
    -webkit-transition: .4s opacity;
    -o-transition: .4s opacity;
    transition: .4s opacity;
    background: -webkit-linear-gradient(270deg, #f07f10, #f010ac, #10f05e, #ebf010, #10eef0, #f01010);
    background: -moz-linear-gradient(270deg, #f07f10, #f010ac, #10f05e, #ebf010, #10eef0, #f01010);
    background: -ms-linear-gradient(270deg, #f07f10, #f010ac, #10f05e, #ebf010, #10eef0, #f01010);
    background: linear-gradient(270deg, #f07f10, #f010ac, #10f05e, #ebf010, #10eef0, #f01010);
    background-size: 1200% 1200%;
    -webkit-animation: gradient 9s ease infinite;
    -moz-animation: gradient 9s ease infinite;
    animation: gradient 9s ease infinite;
}

@-webkit-keyframes gradient {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@-moz-keyframes gradient {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@keyframes gradient {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
.breakout5 .mouse-color-changer:hover img ~ #gradient {opacity: 0;}
.breakout5 .mouse-wrapper .glow {background: url(http://img.stage.creative.com/inline/products/22921/sbxsiege_aurora_ref.jpg) top left;}
.breakout5 .color-changer {
    position: relative;
    height: 7px;
    background: url(http://img.stage.creative.com/inline/products/22921/sbxsiege_aurora_ref.jpg);
    -webkit-background-size: contain;
    background-size: contain;
    width: 60%;
    box-shadow: 2px 2px 3px rgba(0,0,0,0.5) inset, -2px -2px 3px rgba(255,255,255,0.3) inset;
    border-radius: 20px;
    margin: auto;
    z-index: 3;
}
/* For Fat Fingers. :) */
.breakout5 .color-changer:after {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    z-index: -1;
}
.breakout5 .color-changer .handle {
    position: absolute;
    top: 0px;
    left: 0%;
    width: 18px;
    height: 18px;
    background: #b5bdc8;
    border: none;
    outline: none;
    box-shadow: 0 0 3px rgba(0,0,0,.6) inset, 0 0 6px rgba(0,0,0,.4) inset;
    border-radius: 20px;
    margin: -5px -9px;
    padding: 0;
    z-index: 2;
}

/* For Fat Fingers. :) */
.breakout5 .color-changer .handle:after {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    z-index: 1;
}
